import SwiftUI
import RealityKit
import ARKit

struct ARTapPlaceView: UIViewRepresentable {
    class Coordinator: NSObject {
        var view: ARView!
        init(view: ARView) { self.view = view }
        @objc func handleTap(_ sender: UITapGestureRecognizer) {
            let location = sender.location(in: view)
            if let result = view.raycast(from: location, allowing: .existingPlaneInfinite, alignment: .horizontal).first {
                let anchor = AnchorEntity(world: result.worldTransform)
                let sphere = ModelEntity(mesh: .generateSphere(radius: 0.05), materials: [SimpleMaterial(color: .red, isMetallic: false)])
                anchor.addChild(sphere)
                view.scene.addAnchor(anchor)
            }
        }
    }

    func makeCoordinator() -> Coordinator { Coordinator(view: arView) }
    private let arView = ARView(frame: .zero)

    func makeUIView(context: Context) -> ARView {
        let tap = UITapGestureRecognizer(target: context.coordinator, action: #selector(Coordinator.handleTap))
        arView.addGestureRecognizer(tap)
        return arView
    }

    func updateUIView(_ uiView: ARView, context: Context) {}
}

