import SwiftUI
import ARKit
import RealityKit

struct ARPhysicsView: UIViewRepresentable {
    func makeUIView(context: Context) -> ARView {
        let view = ARView(frame: .zero)

        let anchor = AnchorEntity(world: .zero)
        view.scene.addAnchor(anchor)

        let box = ModelEntity(mesh: .generateBox(size: 0.1))
        box.components[PhysicsBodyComponent.self] = .init(massProperties: .default, material: .default, mode: .dynamic)
        box.components[CollisionComponent.self] = .init(shapes: [.generateBox(size: [0.1,0.1,0.1])])

        anchor.addChild(box)
        return view
    }
    func updateUIView(_ uiView: ARView, context: Context) {}
}

