import SwiftUI

struct Particle: Identifiable {
    let id = UUID()
    var position: CGPoint
    var velocity: CGPoint
}

struct ParticleCanvasView: View {
    @State private var particles = (0..<50).map { _ in Particle(position: .zero, velocity: .zero) }

    var body: some View {
        TimelineView(.animation) { timeline in
            Canvas { context, size in
                for i in 0..<particles.count {
                    var particle = particles[i]
                    if particle.position == .zero {
                        particle.position = CGPoint(x: .random(in: 0...size.width), y: .random(in: 0...size.height))
                        particle.velocity = CGPoint(x: .random(in: -2...2), y: .random(in: -2...2))
                    }
                    particle.position.x += particle.velocity.x
                    particle.position.y += particle.velocity.y

                    context.fill(Path(ellipseIn: CGRect(x: particle.position.x, y: particle.position.y, width: 4, height: 4)), with: .color(.orange))
                    particles[i] = particle
                }
            }
        }
        .background(Color.black.ignoresSafeArea())
    }
}

