func fetchNumbers() async -> [Int] {
    await withTaskGroup(of: Int.self) { group in
        for i in 1...5 {
            group.addTask {
                try? await Task.sleep(for: .seconds(Double.random(in: 0.5...2)))
                print("‚úÖ Task \(i) fertig")
                return i * 10
            }
        }
        var results: [Int] = []
        for await number in group { results.append(number) }
        return results
    }
}

