import SwiftUI

struct Fruit: Codable, Identifiable {
    let id = UUID()
    let name: String
}

func loadFruits() -> [Fruit] {
    guard let url = Bundle.main.url(forResource: "fruits", withExtension: "json"),
          let data = try? Data(contentsOf: url) else {
        return []
    }
    return (try? JSONDecoder().decode([Fruit].self, from: data)) ?? []
}

struct JSONExampleView: View {
    let fruits = loadFruits()

