import SwiftUI

struct AsyncImageAdvancedView: View {
    let imageURL = URL(string: "https://picsum.photos/400")!
    @State private var loaded = false

    var body: some View {
        VStack(spacing: 20) {
            AsyncImage(url: imageURL) { phase in
                switch phase {
                case .empty:
                    ProgressView()
                case .success(let image):
                    image.resizable()
                        .scaledToFit()
                        .cornerRadius(12)
                        .opacity(loaded ? 1 : 0)
                        .onAppear { withAnimation(.easeIn(duration: 0.5)) { loaded = true } }
                case .failure:
                    Image(systemName: "xmark.octagon.fill")
                        .font(.largeTitle)
                        .foregroundColor(.red)
                @unknown default:
                    EmptyView()
                }
            }
            .frame(width: 250, height: 250)
        }
        .padding()
    }
}

