import SwiftUI

struct CodePreviewView: View {
    @State private var display: String = "0"
    @State private var lastOperator: String? = nil
    @State private var lastValue: Double? = nil
    @State private var isEnteringNewNumber = false

    private let buttons = [
        ["AC", "+/-", "%", "Ã·"],
        ["7", "8", "9", "Ã—"],
        ["4", "5", "6", "âˆ’"],
        ["1", "2", "3", "+"],
        ["0", ".", "="]
    ]

    var body: some View {
        ZStack {
            LinearGradient(colors: [.black, Color.blue.opacity(0.9)], startPoint: .topLeading, endPoint: .bottomTrailing)
                .ignoresSafeArea()

            VStack(spacing: 18) {
                Spacer()
                HStack {
                    Spacer()
                    Text(display)
                        .font(.system(size: 68, weight: .semibold, design: .rounded))
                        .foregroundColor(.white)
                        .lineLimit(1)
                        .minimumScaleFactor(0.5)
                        .padding()
                        .frame(maxWidth: .infinity, alignment: .trailing)
                }
                .background(Color.white.opacity(0.05))
                .cornerRadius(16)
                .shadow(color: .black.opacity(0.4), radius: 10, y: 5)
                .padding(.horizontal)

                VStack(spacing: 12) {
                    ForEach(buttons, id: \.self) { row in
                        HStack(spacing: 12) {
                            ForEach(row, id: \.self) { symbol in
                                Button(action: { buttonTapped(symbol) }) {
                                    Text(symbol)
                                        .font(.system(size: 28, weight: .semibold, design: .rounded))
                                        .frame(maxWidth: .infinity, minHeight: 70)
                                        .background(buttonColor(for: symbol))
                                        .foregroundColor(.white)
                                        .cornerRadius(40)
                                        .shadow(color: .black.opacity(0.3), radius: 4, y: 2)
                                }
                                .buttonStyle(ScaleButtonStyle())
                            }
                        }
                    }
                }
                .padding(.horizontal)
                .padding(.bottom, 20)
            }
        }
    }

    private func buttonTapped(_ symbol: String) {
        switch symbol {
        case "AC": clear()
        case "+/-": toggleSign()
        case "%": applyPercent()
        case "Ã·", "Ã—", "âˆ’", "+": setOperator(symbol)
        case "=": calculateResult()
        default: enterNumber(symbol)
        }
    }

    private func clear() {
        display = "0"
        lastOperator = nil
        lastValue = nil
        isEnteringNewNumber = false
    }

    private func toggleSign() {
        guard var value = Double(display) else { return }
        value *= -1
        display = String(format: "%.2f", value)
    }

    private func applyPercent() {
        guard var value = Double(display) else { return }
        value /= 100
        display = String(format: "%.2f", value)
    }

    private func setOperator(_ op: String) {
        lastValue = Double(display)
        lastOperator = op
        isEnteringNewNumber = true
    }

    private func calculateResult() {
        guard let lastVal = lastValue, let op = lastOperator, let currentVal = Double(display) else { return }
        var result: Double = 0
        switch op {
        case "+": result = lastVal + currentVal
        case "âˆ’": result = lastVal - currentVal
        case "Ã—": result = lastVal * currentVal
        case "Ã·": result = currentVal != 0 ? lastVal / currentVal : 0
        default: break
        }
        display = String(format: "%g", result)
        lastOperator = nil
        lastValue = nil
    }

    private func enterNumber(_ symbol: String) {
        if isEnteringNewNumber {
            display = (symbol == ".") ? "0." : symbol
            isEnteringNewNumber = false
        } else {
            if display == "0" && symbol != "." {
                display = symbol
            } else {
                display += symbol
            }
        }
    }

    private func buttonColor(for symbol: String) -> Color {
        switch symbol {
        case "AC", "+/-", "%": return Color.gray.opacity(0.5)
        case "Ã·", "Ã—", "âˆ’", "+", "=": return Color.blue
        default: return Color.gray.opacity(0.25)
        }
    }
}

struct ScaleButtonStyle: ButtonStyle {
    func makeBody(configuration: Configuration) -> some View {
        configuration.label
            .scaleEffect(configuration.isPressed ? 0.92 : 1.0)
            .animation(.spring(response: 0.3, dampingFraction: 0.6), value: configuration.isPressed)
    }
}

