import SwiftUI

struct JournalEntry: Identifiable {
    let id = UUID()
    let title: String
    let text: String
    let date: Date
}

let demoEntries: [JournalEntry] = [
    JournalEntry(title: "Erster Tag", text: "Heute habe ich SwiftUI gelernt. Es ist wirklich cool und macht SpaÃŸ!", date: Date()),
    JournalEntry(title: "Zweiter Tag", text: "Ich habe mein erstes eigenes Projekt gestartet und neue Dinge ausprobiert.", date: Date().addingTimeInterval(-86400)),
    JournalEntry(title: "Dritter Tag", text: "Heute habe ich Dark Mode ausprobiert und meine App angepasst.", date: Date().addingTimeInterval(-172800))
]

struct JournalView: View {
    @State private var entries = demoEntries
    @State private var showNewEntry = false

    var body: some View {
        NavigationStack {
            List(entries) { entry in
                NavigationLink(destination: JournalDetailView(entry: entry)) {
                    JournalRow(entry: entry)
                }
            }
            .navigationTitle("ðŸ““ Journal")
            .toolbar {
                ToolbarItem(placement: .navigationBarTrailing) {
                    Button {
                        showNewEntry = true
                    } label: {
                        Image(systemName: "plus.circle.fill")
                            .font(.title2)
                    }
                }
            }
            .sheet(isPresented: $showNewEntry) {
                NewJournalEntryView { newEntry in
                    entries.insert(newEntry, at: 0)
                }
            }
        }
    }
}

struct JournalRow: View {
    let entry: JournalEntry

    var body: some View {
        VStack(alignment: .leading, spacing: 4) {
            Text(entry.title)
                .font(.headline)
            Text(entry.date, style: .date)
                .font(.caption)
                .foregroundColor(.secondary)
            Text(entry.text)
                .font(.subheadline)
                .lineLimit(1)
                .foregroundColor(.gray)
        }
        .padding(.vertical, 4)
    }
}

struct JournalDetailView: View {
    let entry: JournalEntry

    var body: some View {
        ScrollView {
            VStack(alignment: .leading, spacing: 16) {
                Text(entry.title)
                    .font(.title.bold())

                Text(entry.date, style: .date)
                    .font(.caption)
                    .foregroundColor(.secondary)

                Divider()

                Text(entry.text)
                    .font(.body)
                    .lineSpacing(4)
            }
            .padding()
        }
        .navigationTitle("Eintrag")
        .navigationBarTitleDisplayMode(.inline)
    }
}

struct NewJournalEntryView: View {
    @Environment(\.dismiss) private var dismiss

    @State private var title = ""
    @State private var text = ""

    var onSave: (JournalEntry) -> Void

    var body: some View {
        NavigationStack {
            Form {
                Section(header: Text("Titel")) {
                    TextField("Titel des Eintrags", text: $title)
                }
                Section(header: Text("Inhalt")) {
                    TextEditor(text: $text)
                        .frame(height: 150)
                }
            }
            .navigationTitle("Neuer Eintrag")
            .toolbar {
                ToolbarItem(placement: .cancellationAction) {
                    Button("Abbrechen") { dismiss() }
                }
                ToolbarItem(placement: .confirmationAction) {
                    Button("Speichern") {
                        let newEntry = JournalEntry(title: title, text: text, date: Date())
                        onSave(newEntry)
                        dismiss()
                    }
                    .disabled(title.isEmpty || text.isEmpty)
                }
            }
        }
    }
}

