import SwiftUI

struct Product: Identifiable, Hashable {
    let id = UUID()
    let name: String
    let price: Double
    let emoji: String
}

struct ECommerceView: View {
    @State private var products: [Product] = [
        Product(name: "iPhone", price: 999, emoji: "ðŸ“±"),
        Product(name: "MacBook", price: 1999, emoji: "ðŸ’»"),
        Product(name: "AirPods", price: 199, emoji: "ðŸŽ§"),
        Product(name: "Apple Watch", price: 399, emoji: "âŒšï¸"),
        Product(name: "iPad", price: 799, emoji: "ðŸ“Ÿ"),
        Product(name: "HomePod", price: 299, emoji: "ðŸ”Š")
    ]
    @State private var cart: [Product: Int] = [:]
    @State private var showCart = false

    private var cartCount: Int { cart.values.reduce(0, +) }
    private var cartTotal: Double { cart.reduce(0) { $0 + Double($1.value) * $1.key.price } }

    private let columns = [GridItem(.flexible()), GridItem(.flexible())]

    var body: some View {
        ScrollView {
            LazyVGrid(columns: columns, spacing: 16) {
                ForEach(products) { product in
                    ProductCard(product: product) {
                        add(product)
                    }
                }
            }
            .padding()
        }
        .navigationTitle("ðŸ›ï¸ Shop")
        .toolbar {
            ToolbarItem(placement: .topBarTrailing) {
                Button {
                    showCart = true
                } label: {
                    HStack(spacing: 4) {
                        Image(systemName: "bag.fill")
                        if cartCount > 0 {
                            Text("\(cartCount)")
                                .font(.footnote).bold()
                                .padding(4)
                                .background(Capsule().fill(Color.blue.opacity(0.15)))
                        }
                    }
                }
                .accessibilityLabel("Warenkorb")
            }
        }
        .sheet(isPresented: $showCart) {
            NavigationStack {
                CartView(cart: cart,
                         onIncrement: { add($0) },
                         onDecrement: { decrement($0) },
                         onRemove: { remove($0) },
                         total: cartTotal)
                .navigationTitle("Warenkorb")
                .toolbar {
                    ToolbarItem(placement: .topBarTrailing) {
                        Button("Fertig") { showCart = false }
                    }
                }
            }
        }
    }

    private func add(_ product: Product) {
        cart[product, default: 0] += 1
    }

    private func decrement(_ product: Product) {
        guard let qty = cart[product] else { return }
        if qty <= 1 {
            cart.removeValue(forKey: product)
        } else {
            cart[product] = qty - 1
        }
    }

    private func remove(_ product: Product) {
        cart.removeValue(forKey: product)
    }
}

struct ProductCard: View {
    let product: Product
    let addAction: () -> Void

    var body: some View {
        VStack(spacing: 8) {
            Text(product.emoji)
                .font(.system(size: 40))
                .frame(maxWidth: .infinity, minHeight: 80)
                .background(.thinMaterial)
                .clipShape(RoundedRectangle(cornerRadius: 12, style: .continuous))

            VStack(alignment: .leading, spacing: 4) {
                Text(product.name)
                    .font(.headline)
                Text(product.price, format: .currency(code: Locale.current.currency?.identifier ?? "USD"))
                    .font(.subheadline)
                    .foregroundStyle(.secondary)
            }

            HStack {
                Spacer()
                Button(action: addAction) {
                    Label("HinzufÃ¼gen", systemImage: "plus.circle.fill")
                }
                .buttonStyle(.borderedProminent)
            }
        }
        .padding()
        .background(
            RoundedRectangle(cornerRadius: 16, style: .continuous)
                .fill(Color(.systemBackground))
                .shadow(color: .black.opacity(0.05), radius: 8, x: 0, y: 2)
        )
    }
}

struct CartView: View {
    let cart: [Product: Int]
    let onIncrement: (Product) -> Void
    let onDecrement: (Product) -> Void
    let onRemove: (Product) -> Void
    let total: Double

    private var items: [(product: Product, qty: Int)] {
        cart.map { ($0.key, $0.value) }.sorted { $0.product.name < $1.product.name }
    }

    var body: some View {
        List {
            ForEach(items, id: \.product) { item in
                HStack(spacing: 12) {
                    Text(item.product.emoji)
                        .font(.largeTitle)
                    VStack(alignment: .leading) {
                        Text(item.product.name)
                            .font(.headline)
                        Text(item.product.price, format: .currency(code: Locale.current.currency?.identifier ?? "USD"))
                            .foregroundStyle(.secondary)
                    }
                    Spacer()
                    HStack(spacing: 8) {
                        Button(action: { onDecrement(item.product) }) {
                            Image(systemName: "minus.circle.fill").font(.title3)
                        }
                        Text("\(item.qty)")
                            .frame(minWidth: 24)
                        Button(action: { onIncrement(item.product) }) {
                            Image(systemName: "plus.circle.fill").font(.title3)
                        }
                    }
                    Button(role: .destructive, action: { onRemove(item.product) }) {
                        Image(systemName: "trash")
                    }
                }
                .buttonStyle(.plain)
            }

            if items.isEmpty {
                ContentUnavailableView("Warenkorb ist leer", systemImage: "bag", description: Text("FÃ¼gen Sie Produkte hinzu, um fortzufahren."))
            }

            Section {
                HStack {
                    Text("Summe")
                    Spacer()
                    Text(total, format: .currency(code: Locale.current.currency?.identifier ?? "USD")).bold()
                }
                Button {
                    // Demo Checkout
                } label: {
                    Label("Zur Kasse", systemImage: "creditcard.fill")
                }
                .buttonStyle(.borderedProminent)
                .disabled(items.isEmpty)
            }
        }
    }
}

