import { PanGestureHandler } from 'react-native-gesture-handler';
import Animated, { useSharedValue, useAnimatedStyle, withSpring } from 'react-native-reanimated';
import { View } from 'react-native';

export default function DragExample() {
  const x = useSharedValue(0);
  const y = useSharedValue(0);
  const style = useAnimatedStyle(() => ({ transform: [{ translateX: x.value }, { translateY: y.value }] }));

  return (
    <PanGestureHandler onGestureEvent={event => {
      x.value = withSpring(event.translationX);
      y.value = withSpring(event.translationY);
    }}>
      <Animated.View style={[{ width: 80, height: 80, backgroundColor: 'cyan', borderRadius: 10 }, style]} />
    </PanGestureHandler>
  );
}

