import { createContext, useContext, useState } from 'react';
import { View, Text, Button } from 'react-native';

const themes = {
  fire: { background: '#FF3B30', text: '#fff' },
  ice: { background: '#5AC8FA', text: '#000' }
};
const ThemeContext = createContext();

export default function ThemedApp() {
  const [theme, setTheme] = useState('fire');
  return (
    <ThemeContext.Provider value={{ theme, setTheme }}>
      <Screen />
    </ThemeContext.Provider>
  );
}

function Screen() {
  const { theme, setTheme } = useContext(ThemeContext);
  const t = themes[theme];
  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center', backgroundColor: t.background }}>
      <Text style={{ color: t.text }}>üî• Aktuelles Theme: {theme}</Text>
      <Button title="Wechsel zu Ice" onPress={() => setTheme(theme === 'fire' ? 'ice' : 'fire')} />
    </View>
  );
}

