func getTimeline(in context: Context, completion: @escaping (Timeline<Entry>) -> Void) {
    var entries: [Entry] = []
    for i in 0..<10 {
        entries.append(Entry(date: .now.addingTimeInterval(Double(i)), value: i))
    }
    completion(Timeline(entries: entries, policy: .after(.now.addingTimeInterval(10))))
}

import WidgetKit
import SwiftUI

struct WeatherEntry: TimelineEntry { let date: Date; let temp: Int }

struct WeatherProvider: TimelineProvider {
    func placeholder(in context: Context) -> WeatherEntry { WeatherEntry(date: .now, temp: 0) }

    func getSnapshot(in context: Context, completion: @escaping (WeatherEntry) -> Void) {
        completion(WeatherEntry(date: .now, temp: 14))
    }

    func getTimeline(in context: Context, completion: @escaping (Timeline<WeatherEntry>) -> Void) {
        Task {
            let temp = await fetchTemp()
            let entry = WeatherEntry(date: .now, temp: temp)
            completion(Timeline(entries: [entry], policy: .after(.now.addingTimeInterval(900))))
        }
    }

    func fetchTemp() async -> Int {
        / Fake API
        return Int.random(in: -5...30)
    }
}

