import WidgetKit
import SwiftUI

struct SimpleEntry: TimelineEntry {
    let date: Date
}

struct Provider: TimelineProvider {
    func placeholder(in context: Context) -> SimpleEntry { SimpleEntry(date: Date()) }
    func getSnapshot(in context: Context, completion: @escaping (SimpleEntry) -> ()) {
        completion(SimpleEntry(date: Date()))
    }
    func getTimeline(in context: Context, completion: @escaping (Timeline<SimpleEntry>) -> ()) {
        let entries = [SimpleEntry(date: .now)]
        completion(Timeline(entries: entries, policy: .after(.now.addingTimeInterval(60))))
    }
}

struct MyWidgetEntryView: View {
    var entry: Provider.Entry
    var body: some View {
        VStack {
            Text(entry.date, style: .time)
                .font(.title)
            Text("‚è∞ Swift Widget!")
        }
    }
}

@main
struct MyWidget: Widget {
    var body: some WidgetConfiguration {
        StaticConfiguration(kind: "my_widget", provider: Provider()) { entry in
            MyWidgetEntryView(entry: entry)
        }
        .configurationDisplayName("Zeit Widget")
        .description("Zeigt die aktuelle Uhrzeit an.")
    }
}

import WidgetKit
import SwiftUI

struct SimpleNoteEntry: TimelineEntry { let date: Date; let note: String }

struct NoteProvider: TimelineProvider {
    func placeholder(in context: Context) -> SimpleNoteEntry { SimpleNoteEntry(date: .now, note: "...") }
    func getSnapshot(in context: Context, completion: @escaping (SimpleNoteEntry) -> Void) {
        let note = UserDefaults(suiteName: "group.app")?.string(forKey: "note") ?? "Keine Notiz"
        completion(SimpleNoteEntry(date: .now, note: note))
    }
    func getTimeline(in context: Context, completion: @escaping (Timeline<SimpleNoteEntry>) -> Void) {
        let note = UserDefaults(suiteName: "group.app")?.string(forKey: "note") ?? "Keine Notiz"
        let entry = SimpleNoteEntry(date: .now, note: note)
        completion(Timeline(entries: [entry], policy: .never))
    }
}

struct NoteWidgetView: View {
    var entry: SimpleNoteEntry
    var body: some View {
        Text(entry.note).padding()
    }
}

@main
struct NoteWidget: Widget {
    var body: some WidgetConfiguration {
        StaticConfiguration(kind: "note_widget", provider: NoteProvider()) { entry in
            NoteWidgetView(entry: entry)
        }
    }
}

