import WidgetKit
import SwiftUI

struct SimpleEntry: TimelineEntry {
    let date: Date
}

struct Provider: TimelineProvider {
    func placeholder(in context: Context) -> SimpleEntry { SimpleEntry(date: Date()) }
    func getSnapshot(in context: Context, completion: @escaping (SimpleEntry) -> ()) {
        completion(SimpleEntry(date: Date()))
    }
    func getTimeline(in context: Context, completion: @escaping (Timeline<SimpleEntry>) -> ()) {
        let entries = [SimpleEntry(date: .now)]
        completion(Timeline(entries: entries, policy: .after(.now.addingTimeInterval(60))))
    }
}

struct MyWidgetEntryView: View {
    var entry: Provider.Entry
    var body: some View {
        VStack {
            Text(entry.date, style: .time)
                .font(.title)
            Text("⏰ Swift Widget!")
        }
    }
}

@main
struct MyWidget: Widget {
    var body: some WidgetConfiguration {
        StaticConfiguration(kind: "my_widget", provider: Provider()) { entry in
            MyWidgetEntryView(entry: entry)
        }
        .configurationDisplayName("Zeit Widget")
        .description("Zeigt die aktuelle Uhrzeit an.")
    }
}
