class TapCoordinator: NSObject {
    var arView: ARView?
    @objc func handleTap(_ sender: UITapGestureRecognizer) {
        guard let view = arView else { return }
        let location = sender.location(in: view)
        if let result = view.raycast(from: location, allowing: .estimatedPlane, alignment: .horizontal).first {
            let anchor = AnchorEntity(world: result.worldTransform)
            let sphere = ModelEntity(mesh: .generateSphere(radius: 0.05), materials: [SimpleMaterial(color: .cyan, isMetallic: false)])
            anchor.addChild(sphere)
            view.scene.addAnchor(anchor)
        }
    }
}

struct TapPlaceARView: UIViewRepresentable {
    let coordinator = TapCoordinator()

    func makeUIView(context: Context) -> ARView {
        let view = ARView(frame: .zero)
        coordinator.arView = view
        let tap = UITapGestureRecognizer(target: coordinator, action: #selector(TapCoordinator.handleTap))
        view.addGestureRecognizer(tap)

        let config = ARWorldTrackingConfiguration()
        config.planeDetection = [.horizontal]
        view.session.run(config)
        return view
    }
    func updateUIView(_ uiView: ARView, context: Context) {}
}

