class PlaneDelegate: NSObject, ARSessionDelegate {
    var arView: ARView?
    func session(_ session: ARSession, didAdd anchors: [ARAnchor]) {
        for anchor in anchors {
            if let planeAnchor = anchor as? ARPlaneAnchor,
               let view = arView {
                let plane = ModelEntity(mesh: .generatePlane(width: planeAnchor.extent.x, height: planeAnchor.extent.z),
                                        materials: [SimpleMaterial(color: .blue.opacity(0.4), isMetallic: false)])
                let anchorEntity = AnchorEntity(anchor: planeAnchor)
                anchorEntity.addChild(plane)
                view.scene.addAnchor(anchorEntity)
            }
        }
    }
}

