class GestureCoordinator: NSObject {
    var model: ModelEntity?

    @objc func pinch(_ g: UIPinchGestureRecognizer) {
        model?.scale *= Float(g.scale)
        g.scale = 1.0
    }

    @objc func rotate(_ g: UIRotationGestureRecognizer) {
        model?.transform.rotation *= simd_quatf(angle: Float(g.rotation), axis: [0,1,0])
        g.rotation = 0
    }
}

struct GestureARView: UIViewRepresentable {
    let coordinator = GestureCoordinator()

    func makeUIView(context: Context) -> ARView {
        let view = ARView(frame: .zero)

        let model = try! ModelEntity.load(named: "toy_robot")
        coordinator.model = model
        let anchor = AnchorEntity(world: [0,0,-0.5])
        anchor.addChild(model)
        view.scene.addAnchor(anchor)

        view.addGestureRecognizer(UIPinchGestureRecognizer(target: coordinator, action: #selector(GestureCoordinator.pinch)))
        view.addGestureRecognizer(UIRotationGestureRecognizer(target: coordinator, action: #selector(GestureCoordinator.rotate)))

        return view
    }

    func updateUIView(_ uiView: ARView, context: Context) {}
}

