struct ARPhysicsView: UIViewRepresentable {
    func makeUIView(context: Context) -> ARView {
        let view = ARView(frame: .zero)

        let anchor = AnchorEntity(world: [0,0,-1])

        / Boden
        let floor = ModelEntity(mesh: .generatePlane(width: 2, height: 2), materials: [SimpleMaterial(color: .gray, isMetallic: false)])
        floor.collision = CollisionComponent(shapes: [.generatePlane(width: 2, height: 2)])
        floor.physicsBody = PhysicsBodyComponent(mode: .static)
        anchor.addChild(floor)

        / Kugel
        let sphere = ModelEntity(mesh: .generateSphere(radius: 0.1), materials: [SimpleMaterial(color: .cyan, isMetallic: false)])
        sphere.position = [0, 1, 0]
        sphere.physicsBody = PhysicsBodyComponent(mode: .dynamic)
        sphere.collision = CollisionComponent(shapes: [.generateSphere(radius: 0.1)])
        anchor.addChild(sphere)

        view.scene.addAnchor(anchor)
        return view
    }
