import SwiftUI
import MetalKit

struct MetalBackgroundView: UIViewRepresentable {
    func makeUIView(context: Context) -> MTKView {
        let view = MTKView()
        view.device = MTLCreateSystemDefaultDevice()
        view.clearColor = MTLClearColor(red: 0.2, green: 0.0, blue: 0.4, alpha: 1)
        view.framebufferOnly = true
        return view
    }
    func updateUIView(_ uiView: MTKView, context: Context) {}
}

struct MetalExampleView: View {
    var body: some View {
        MetalBackgroundView()
            .ignoresSafeArea()
            .overlay(
                Text("Metal Shader! ðŸ”¥")
                    .font(.largeTitle)
                    .foregroundColor(.white)
                    .shadow(radius: 10)
            )
    }
}

