import SwiftUI
import RealityKit

struct USDZModelView: View {
    var body: some View {
        RealityView { content in
            if let model = try? await ModelEntity(named: "spirit1") {
                let anchor = AnchorEntity(world: [0,0,-0.5])
                anchor.addChild(model)
                content.add(anchor)
            }
        }
        .ignoresSafeArea()
    }
}

#Preview { USDZModelView() }

if let model = try? await ModelEntity(named: "character") {
    if let animation = model.availableAnimations.first {
        model.playAnimation(animation.repeat())
    }
}

let light = DirectionalLight()
light.light.intensity = 30000
light.light.color = .white
let lightAnchor = AnchorEntity(world: [0,1,0])
lightAnchor.addChild(light)

let sphere = ModelEntity(mesh: .generateSphere(radius: 0.1))
sphere.components.set(CollisionComponent(shapes: [.generateSphere(radius: 0.1)]))
sphere.components.set(PhysicsBodyComponent(massProperties: .default, material: .default, mode: .dynamic))

let tap = UITapGestureRecognizer(target: context.coordinator, action: #selector(Coordinator.handleTap))
arView.addGestureRecognizer(tap)

@objc func handleTap(_ sender: UITapGestureRecognizer) {
   let location = sender.location(in: arView)
   if let result = arView.raycast(from: location, allowing: .estimatedPlane, alignment: .any).first {
      print("Treffer!", result.worldTransform)
   }
}

let subscription = arView.scene.subscribe(to: CollisionEvents.Began.self) { event in
    print("üí• Kollision zwischen", event.entityA, event.entityB)
}

