let light = DirectionalLight()
light.light.intensity = 40000
light.shadow = DirectionalLightComponent.Shadow(maxDistance: 5, depthBias: 1)
let anchor = AnchorEntity(world: [0,1,0])
anchor.addChild(light)

let texture = try! await TextureResource.load(named: "sky.hdr")
let environment = try! await EnvironmentResource(texture: texture)
arView.environment.scene = environment

config.sceneReconstruction = .mesh

arView.session.delegate = context.coordinator

func session(_ session: ARSession, didAdd anchors: [ARAnchor]) {
   for a in anchors {
       if let mesh = a as? ARMeshAnchor {
           let entity = ModelEntity(mesh: .init(mesh: mesh.geometry, device: arView.device!))
           arView.scene.addAnchor(AnchorEntity(anchor: mesh))
       }
   }
}
