import SwiftUI

struct AnimationExampleView: View {
    @State private var scale: CGFloat = 1.0

    var body: some View {
        VStack(spacing: 30) {
            Circle()
                .fill(Color.orange)
                .frame(width: 120, height: 120)
                .scaleEffect(scale)
                .animation(.spring(), value: scale)

            Button("Animate") {
                scale = scale == 1 ? 1.5 : 1
            }
            .buttonStyle(.borderedProminent)
        }
    }
}

