import SwiftUI

struct GridExampleView: View {
    let columns = [
        GridItem(.flexible()),
        GridItem(.flexible()),
        GridItem(.flexible())
    ]

    var body: some View {
        ScrollView {
            LazyVGrid(columns: columns, spacing: 20) {
                ForEach(1...12, id: \.self) { i in
                    RoundedRectangle(cornerRadius: 12)
                        .fill(Color.blue)
                        .frame(height: 80)
                        .overlay(Text("Item \(i)").foregroundColor(.white))
                }
            }
            .padding()
        }
    }
}

