import SwiftUI

struct TextExampleView: View {
    var body: some View {
        VStack(spacing: 20) {
            Text("Hallo SwiftUI!")
                .font(.largeTitle)
                .fontWeight(.bold)
                .foregroundColor(.purple)

            Text("Willkommen zu deiner ersten App!")
                .font(.headline)
                .foregroundColor(.gray)
        }
    }
}

