import SwiftUI

struct ListExampleView: View {
    let namen = ["Anna", "Luca", "Mara", "Timo"]

    var body: some View {
        List(namen, id: \.self) { name in
            Text(name)
        }
        .navigationTitle("Namenliste")
    }
}

