import SwiftUI

struct ToggleExampleView: View {
    @State private var isOn = false

    var body: some View {
        VStack(spacing: 20) {
            Toggle("Schalter", isOn: $isOn)
                .padding()

            Text(isOn ? "Aktiviert" : "Deaktiviert")
                .font(.title2)
                .foregroundColor(isOn ? .green : .red)
        }
        .padding()
    }
}

