import SwiftUI
import SpriteKit

class FireScene: SKScene {
    override func didMove(to view: SKView) {
        backgroundColor = .black

        // ðŸ”¥ Beispiel: Feuerpartikel laden
        if let node = SKEmitterNode(fileNamed: "FireParticle.sks") {
            node.position = CGPoint(x: size.width / 2, y: size.height / 2)
            addChild(node)
        }
    }
}

struct SpriteKitExampleView: View {
    var scene: SKScene {
        let scene = FireScene()
        scene.size = CGSize(width: 400, height: 600)
        scene.scaleMode = .resizeFill
        return scene
    }

    var body: some View {
        SpriteView(scene: scene)
            .ignoresSafeArea()
            .overlay(
                Text("SpriteKit ðŸ”¥")
                    .font(.title)
                    .foregroundColor(.white)
                    .shadow(radius: 8),
                alignment: .top
            )
    }
}

