import SpriteKit

class HeroScene: SKScene {
    override func didMove(to view: SKView) {
        backgroundColor = .black

        let atlas = SKTextureAtlas(named: "Hero")
        var frames: [SKTexture] = []

        for i in 1...6 {
            frames.append(atlas.textureNamed("hero_\(i)"))
        }

        let sprite = SKSpriteNode(texture: frames.first)
        sprite.position = CGPoint(x: size.width / 2, y: size.height / 2)
        addChild(sprite)

        let animation = SKAction.repeatForever(
            SKAction.animate(with: frames, timePerFrame: 0.08)
        )
        sprite.run(animation)
    }
}

import SpriteKit

struct PhysicsCategory {
    static let player: UInt32 = 1
    static let enemy: UInt32 = 2
}

class PhysicsScene: SKScene, SKPhysicsContactDelegate {
    override func didMove(to view: SKView) {
        backgroundColor = .black
        physicsWorld.contactDelegate = self

        let player = SKSpriteNode(color: .cyan, size: CGSize(width: 40, height: 40))
        player.position = CGPoint(x: 200, y: 400)
        player.physicsBody = SKPhysicsBody(rectangleOf: player.size)
        player.physicsBody?.categoryBitMask = PhysicsCategory.player
        player.physicsBody?.contactTestBitMask = PhysicsCategory.enemy
        addChild(player)

        let enemy = SKSpriteNode(color: .red, size: CGSize(width: 40, height: 40))
        enemy.position = CGPoint(x: 200, y: 100)
        enemy.physicsBody = SKPhysicsBody(rectangleOf: enemy.size)
        enemy.physicsBody?.categoryBitMask = PhysicsCategory.enemy
        addChild(enemy)
    }

    func didBegin(_ contact: SKPhysicsContact) {
        print("üí• Kollision erkannt!")
    }
}

import SpriteKit

class ExplosionScene: SKScene {
    override func didMove(to view: SKView) {
        backgroundColor = .black

        let explosion = SKEmitterNode(fileNamed: "Explosion.sks")!
        explosion.position = CGPoint(x: size.width / 2, y: size.height / 2)
        addChild(explosion)

        run(SKAction.sequence([
            SKAction.wait(forDuration: 2),
            SKAction.run { explosion.removeFromParent() }
        ]))
    }
}

import SpriteKit

class TouchMoveScene: SKScene {
    let hero = SKSpriteNode(color: .yellow, size: CGSize(width: 50, height: 50))

    override func didMove(to view: SKView) {
        backgroundColor = .black
        hero.position = CGPoint(x: size.width/2, y: size.height/2)
        addChild(hero)
    }

    override func touchesBegan(_ touches: Set<UITouch>, with event: UIEvent?) {
        guard let touch = touches.first else { return }
        let location = touch.location(in: self)

        let move = SKAction.move(to: location, duration: 0.3)
        move.timingMode = .easeOut

        hero.run(move)
    }
}

import SpriteKit

class ParallaxScene: SKScene {
    var layers: [SKSpriteNode] = []

    override func didMove(to view: SKView) {
        backgroundColor = .black

        for i in 0..<3 {
            let layer = SKSpriteNode(imageNamed: "layer_\(i)")
            layer.position = CGPoint(x: size.width/2, y: size.height/2)
            layer.zPosition = CGFloat(i) * -1
            addChild(layer)
            layers.append(layer)
        }
    }

    override func update(_ currentTime: TimeInterval) {
        for (i, layer) in layers.enumerated() {
            layer.position.x -= CGFloat(i + 1)
            if layer.position.x < -layer.size.width {
                layer.position.x += layer.size.width * 2
            }
        }
    }
}
