import SwiftUI
import SwiftData

// 1️⃣ Datenmodell definieren
@Model
class Note {
    var title: String
    var createdAt: Date

    init(title: String, createdAt: Date = .now) {
        self.title = title
        self.createdAt = createdAt
    }
}

// 2️⃣ Haupt-App mit ModelContainer
@main
struct NotesApp: App {
    var body: some Scene {
        WindowGroup {
            ContentView()
        }
        .modelContainer(for: Note.self)
    }
}

// 3️⃣ Daten in SwiftUI anzeigen und speichern
struct ContentView: View {
    @Environment(\.modelContext) private var context
    @Query private var notes: [Note]
    @State private var newTitle = ""

    var body: some View {
        NavigationStack {
            VStack(spacing: 20) {
                TextField("Neue Notiz", text: $newTitle)
                    .textFieldStyle(.roundedBorder)
                    .padding()

                Button("Speichern") {
                    let note = Note(title: newTitle)
                    context.insert(note)
                    try? context.save()
                    newTitle = ""
                }
                .buttonStyle(.borderedProminent)

                List(notes) { note in
                    VStack(alignment: .leading) {
                        Text(note.title)
                            .font(.headline)
                        Text(note.createdAt, style: .date)
                            .font(.caption)
                            .foregroundColor(.gray)
                    }
                }
            }
            .navigationTitle("SwiftData Notizen")
        }
    }
}

