import WidgetKit
import SwiftUI

// MARK: - Datenmodell
struct SimpleEntry: TimelineEntry {
    let date: Date
}

// MARK: - Provider
struct Provider: TimelineProvider {
    func placeholder(in context: Context) -> SimpleEntry {
        SimpleEntry(date: Date())
    }

    func getSnapshot(in context: Context, completion: @escaping (SimpleEntry) -> ()) {
        completion(SimpleEntry(date: Date()))
    }

    func getTimeline(in context: Context, completion: @escaping (Timeline<SimpleEntry>) -> ()) {
        let entries = [SimpleEntry(date: Date())]
        completion(Timeline(entries: entries, policy: .atEnd))
    }
}

// MARK: - Widget View
struct WidgetKitExampleEntryView : View {
    var entry: Provider.Entry

    var body: some View {
        ZStack {
            LinearGradient(colors: [.blue, .purple], startPoint: .topLeading, endPoint: .bottomTrailing)
            VStack {
                Text("WidgetKit ðŸ”·")
                    .font(.headline)
                    .foregroundColor(.white)
                Text(entry.date, style: .time)
                    .foregroundColor(.white.opacity(0.8))
            }
        }
    }
}

// MARK: - Widget Registrierung
@main
struct WidgetKitExample: Widget {
    let kind: String = "WidgetKitExample"

    var body: some WidgetConfiguration {
        StaticConfiguration(kind: kind, provider: Provider()) { entry in
            WidgetKitExampleEntryView(entry: entry)
        }
        .configurationDisplayName("Beispiel-Widget")
        .description("Ein einfaches Widget mit Zeitangabe.")
        .supportedFamilies([.systemSmall])
    }
}

